--INSERINDO ASSUNTOS FINALÍSTICOS DO ÓRGÃO



--CRIANDO O MAPEAMENTO



--CRIANDO O MAPEAMENTO DOS ASSUNTOS FINALISTICOS DO ORGAO

SELECT  A1.ID_ASSUNTO, A2.ID_ASSUNTO FROM ASSUNTO A1 JOIN (SELECT ID_ASSUNTO, CODIGO_ESTRUTURADO FROM ASSUNTO WHERE ID_TABELA_ASSUNTOS = (SELECT MAX(ID_TABELA_ASSUNTOS) FROM TABELA_ASSUNTOS)) A2 ON A1.CODIGO_ESTRUTURADO = A2.CODIGO_ESTRUTURADO AND A1.ID_TABELA_ASSUNTOS = (SELECT ID_TABELA_ASSUNTOS FROM TABELA_ASSUNTOS TA WHERE SIN_ATUAL ='S') AND SUBSTR(A1.CODIGO_ESTRUTURADO, 1, 1) NOT IN 
('0','9');



